#include<iostream>
#include<vector>

using namespace std;

class Solution {
public:
    int numDistinct(string s, string t) {
        vector<vector<int>> dp(s.size() + 1, vector<int>(t.size() + 1, 0));
        for (int i = 0; i < s.size(); ++i) {
            dp[i][0] = 1;
        }
        for (int i = 1; i <= s.size(); ++i) {
            for (int j = 1; j <= t.size(); ++j) {
                if (j > i) {
                    break;
                }
                dp[i][j] = dp[i - 1][j];
                if (s[i - 1] == t[j - 1]) {
                    dp[i][j] += dp[i - 1][j - 1];
                }
                //´òÓ¡dpÊý×é
                //if(i<10)
                //cout << dp[i][j] << "\t";
            }
            //if(i<10)
            //cout << endl;
        }
        return dp[s.size()][t.size()];
    }
};

